mapper.select:
select

from fee

	fee_type = #{feeType}
	and product_id = #{productId}
	and state = #{state}

mapper.update:
update fee
fee_type=#{feeType, typeHandler=org.apache.ibatis.type.EnumOrdinalTypeHandler},
product_id=#{productId}, name=#{name}, state=#{state}, from_date=#{fromDate}, to_date=#{toDate}, tax_type=#{taxType, typeHandler=team.asd.handler.TaxTypeEnumTypeHandler}, unit=#{unit, typeHandler=team.asd.handler.UnitEnumTypeHandler}, value=#{value},
value_type=#{valueType, typeHandler=team.asd.handler.ValueTypeEnumTypeHandler},
currency=#{currency}
where id = #{id}

mapper.delete:
update fee
set state='Final'
where id = #{id}
